    private void jButtonEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonEntrarActionPerformed
        String usuario=jTextFieldUsuario.getText();
        String password=jPasswordFieldPassword.getText();
                
        if(usuario.equals("admin") && password.equals("123")){
            PrincipalJFrame princAdmin=new PrincipalJFrame();
            princAdmin.setVisible(true);
            this.dispose();
        }else {
            JOptionPane.showMessageDialog(null,"Usuario/Contrase√±a incorecta");
        }
        
    }//GEN-LAST:event_jButtonEntrarActionPerformed

    private void jButtonSalirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonSalirActionPerformed
        int dialog=JOptionPane.YES_NO_OPTION;
        int result=JOptionPane.showConfirmDialog(null,"Desea salir del programa?","Salir",dialog);
        if(result==0){
            System.exit(0);
        }
    }//GEN-LAST:event_jButtonSalirActionPerformed

